@media screen and (max-width: 767px) {
  .footer {
    width: 100%;
    background-color: var(--color-primary);
    padding-top: 100px;
    padding-bottom: 100px;
  }

  .footer-logo {
    font-family: Raleway, sans-serif;
    font-size: 18px;
    font-style: normal;
    font-weight: 700;
    line-height: 1.17; /* 116.667% */
    letter-spacing: 0.03em;
    text-transform: uppercase;
    color: var(--color-brand);
    margin-bottom: 16px;
  }
  .footer-logo .accent {
    color: var(--color-light);
  }
  .footer-marketing {
    max-width: 288px;
    line-height: 1.5;
    letter-spacing: 0.02em;
    color: var(--color-light);
    margin-top: 16px;
  }

  .cont-footer {
    width: 320px;
    display: block;
    gap: 72px;
  }

  .container-one {
    text-align: center;
  }

  .container-two {
    display: block;
    padding-left: 60px;
    align-items: center;
    gap: 16px;
  }

  .footer-social {
    font-size: 16px;
    font-weight: 500;
    line-height: 1.5; /* 150% */
    letter-spacing: 0.02em;
    color: #fff;
    margin-top: 72px;
    margin-bottom: 16px;
  }
  .footer-media {
    max-width: 96px;
    border-radius: 50%;
    color: var(--color-brand);
    display: flex;
    width: 100%;
    height: 100%;
    align-items: center;
    justify-content: center;
    background-color: var(--color-brand);
    transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1);
  }

  .footer-media:hover,
  .footer-media:focus {
    background-color: var(--color-green);
  }

  .icon-list-footer {
    display: flex;
    gap: 16px;
    margin-bottom: 72px;
  }
  .icon-social {
    width: 40px;
    height: 40px;
  }
  .container-footer {
    padding-top: 0px;
    padding-bottom: 0px;
  }
  .footer-list-soc {
    fill: var(--color-light);
  }
  .footer-subscribe-form {
    display: block;
    gap: 16px;
  }
  .footer-inputdiv {
    display: block;
    align-items: center;
    justify-content: center;
  }
  .footer-subscribe-text {
    color: var(--color-hero);
    font-size: 16px;
    font-weight: 500;
    line-height: 1.5; /* 150% */
    letter-spacing: 0.02em;
    margin-bottom: 16px;

  }
  .footer-input-email {
    width: 288px;
    height: 40px;
    border-radius: 4px;
    border: 1px solid var(--color-hero);
    box-shadow: 0px 4px 4px 0px rgba(0, 0, 0, 0.15);
    background-color: var(--color-primary);
    color: var(--color-hero);
    background-color: transparent;
    font-size: 12px;
    line-height: 1.5;
    letter-spacing: 0.04em;
    padding-left: 16px;
    filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.15));
  }
  .footer-subscribe-button {
    display: flex;
    padding: 8px 24px;
    font-size: 16px;
    font-weight: 500;
    border-radius: 4px;
    background: var(--color-brand);
    border: none;
    max-width: 100%;
    color: var(--color-hero);
    text-align: center;
    line-height: 1.5;
    letter-spacing: 0.04em;
    transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1);
    box-shadow: 0px 4px 4px 0px rgba(0, 0, 0, 0.15);
    margin-top: 16px;
    min-width: 165px;
    justify-content: center;
    align-items: center;
  }
  .footer-subscribe-svg {
    margin-left: 16px;
  }
}

